import { __decorate } from "tslib";
import { Component } from '@angular/core';
let UserListComponent = class UserListComponent {
    constructor(userListServices) {
        this.userListServices = userListServices;
        this.userList = [];
        this.temperatures = [];
        this.loading = true;
        this.pagingInfo = { pageSize: 10, currentPage: 0, pageCount: 0 };
        this.pageArray = [];
    }
    ngOnInit() {
        this.delay(500);
        this.getProductsByPage(2);
    }
    async delay(ms) {
        await new Promise(resolve => setTimeout(() => resolve(), ms)).then(() => {
            this.userListServices.getProductsByPage(2).subscribe(data => {
                this.loading = false;
                this.pagingInfo.pageCount = Math.ceil(data.length / this.pagingInfo.pageSize);
                this.generatePageArray(this.pagingInfo.pageCount);
                console.log(this.pageArray);
            });
        });
    }
    generatePageArray(pageCount) {
        for (let i = 0; i < pageCount; i++) {
            this.pageArray.push(i);
        }
    }
    getProductsByPage(page) {
        this.userListServices.getProductsByPage(page).subscribe(data => {
            var datas = JSON.parse(JSON.stringify(data));
            console.log(datas.data);
            let ss = data;
            this.userList = datas.data;
            this.pagingInfo.currentPage = page;
        });
    }
};
UserListComponent = __decorate([
    Component({
        selector: 'app-user-list',
        templateUrl: './user-list.component.html',
        styleUrls: ['./user-list.component.css']
    })
], UserListComponent);
export { UserListComponent };
